import React, { useState } from "react";
import "../KanbanBoard/KanbanBoard.module.scss";
function KanbanBoard() {
  const [columns, setColumns] = useState([
    {
      id: 1,
      title: "сделать",
      items: [
        { id: 1, title: "Пойти какать" },
        { id: 2, title: "Пойти какать2" },
      ],
    },
    {
      id: 2,
      title: "какать",
      items: [
        { id: 3, title: "Пойти какать3" },
        { id: 4, title: "Пойти какать4" },
      ],
    },
    {
      id: 3,
      title: "готово",
      items: [
        { id: 5, title: "Пойти какать5" },
        { id: 6, title: "Пойти какать6" },
      ],
    },
  ]);

  function dragOverHandler(e, board, item) {
    e.preventDefault();
    if (e.target.className == "item") {
    }
  }
  function dragLeaveHandler() {}
  function dragStartHandler() {}
  function dragEndHandler() {}
  function dragDropHandler() {
    e.preventDefault();
  }

  return (
    <div>
      <div className={"board"}>
        {columns.map((column) => (
          <div className={"column"}>
            <div className={"columnTitle"}>{column.title}</div>
            {column.items.map((item) => (
              <div
                onDragOver={(e) => dragOverHandler(e, column, item)}
                onDragLeave={(e) => dragLeaveHandler(e, column, item)}
                onDragStart={(e) => dragStartHandler(e, column, item)}
                onDragEnd={(e) => dragEndHandler(e, column, item)}
                onDrop={(e) => dragDropHandler(e, column, item)}
                draggable={true}
                className={"item"}
              >
                {item.title}
              </div>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
}

export default KanbanBoard;
